mapper.select:
SELECT ROWNUM, NUM, TITLE, CONTENT, WRITER, PW, REGIDATE, CHECKDELETE ,PARENT, ORD, DEPTH FROM ( SELECT @ROWNUM:=@ROWNUM+1 as ROWNUM, T.* FROM ( SELECT A.* FROM T_BOARD A , (SELECT @ROWNUM:=0) R WHERE ((CHECKDELETE IS NULL AND PARENT IS NULL) OR (PARENT IS NOT NULL))
AND (TITLE LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY IF(ISNULL(PARENT),NUM,PARENT) DESC, ORD IS NULL DESC, ORD DESC ) T ) S WHERE ROWNUM BETWEEN #{beginRow} AND #{endRow}

mapper.update:
UPDATE T_BOARD 
SET TITLE = #{title},
	CONTENT = #{content},
	WRITER = #{writer},
	PW = #{pw}
WHERE NUM = #{num};

mapper.select:
SELECT MAX(ORD) 
FROM T_BOARD
WHERE PARENT LIKE #{parent}

SELECT COUNT(*) FROM ( SELECT @ROWNUM:=@ROWNUM+1 as ROWNUM, T.* FROM ( SELECT A.* FROM T_BOARD A , (SELECT @ROWNUM:=0) R WHERE ((CHECKDELETE IS NULL AND PARENT IS NULL) OR (PARENT IS NOT NULL))
AND (TITLE LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY IF(ISNULL(PARENT),NUM,PARENT) DESC, ORD IS NULL DESC, ORD DESC ) T ) S WHERE PARENT IS NULL AND ROWNUM BETWEEN 1 AND #{beforePageEndRow}

mapper.insert:
INSERT INTO COMMENTS (BR_NO, CMT_WRITER, CMT_PW, CMT_CONTENT)
VALUES (
	#{num},
	#{cmt_writer},
	#{cmt_pw},
	#{cmt_content}
);

mapper.select:
SELECT * FROM COMMENTS
WHERE BR_NO LIKE #{num}
ORDER BY CMT_NO

mapper.update:
UPDATE COMMENTS 
	SET CMT_WRITER = #{cmt_writer},
		CMT_PW = #{cmt_pw},
		CMT_CONTENT = #{cmt_content}
	WHERE BR_NO LIKE #{num} AND CMT_NO LIKE #{cmt_no}
