mapper.select:
SELECT ROWNUM, NUM, TITLE, CONTENT, WRITER, PW, REGIDATE, CHECKDELETE FROM ( SELECT @ROWNUM:=@ROWNUM+1 as ROWNUM, T.* FROM ( SELECT A.* FROM T_BOARD A , (SELECT @ROWNUM:=0) R WHERE CHECKDELETE IS NULL
AND TITLE LIKE CONCAT('%', #{keyword}, '%')
ORDER BY NUM DESC ) T ) S WHERE ROWNUM BETWEEN #{beginRow} AND #{endRow}

SELECT NUM, TITLE, CONTENT, WRITER,PW, REGIDATE 
FROM T_BOARD
WHERE NUM LIKE #{num}

mapper.update:
UPDATE T_BOARD 
SET TITLE = #{title},
	CONTENT = #{content},
	WRITER = #{writer},
	PW = #{pw}
WHERE NUM = #{num};

UPDATE T_BOARD
SET CHECKDELETE ="Y"
WHERE NUM =#{num};

mapper.insert:
INSERT INTO COMMENTS (BR_NO, CMT_WRITER, CMT_PW, CMT_CONTENT)
VALUES (
	#{num},
	#{cmt_writer},
	#{cmt_pw},
	#{cmt_content}
);

mapper.select:
SELECT * FROM COMMENTS
WHERE BR_NO LIKE #{num}
ORDER BY CMT_NO

mapper.update:
UPDATE COMMENTS 
	SET CMT_WRITER = #{cmt_writer},
		CMT_PW = #{cmt_pw},
		CMT_CONTENT = #{cmt_content}
	WHERE BR_NO = #{num};
